import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const ForumsContainer = styled(motion.div)`
  min-height: 100vh;
  padding: 180px 20px 60px;
  background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuMjHxIGmVAAAAMElEQVQYV2NgoBJwcnJyanJycvL///8/AQwMTEwsLCwsLCwsLCwsLCwsLCwsLCwsDI4GAV8HAFrpV3gAAAAASUVORK5CYII=') repeat;
  background-size: 16px 16px;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 clamp(20px, 5vw, 50px);
`;

const ForumHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 40px;
  
  h1 {
    font-family: 'Press Start 2P', cursive;
    font-size: clamp(1.8em, 5vw, 3em);
    color: #ff4081;
    text-transform: uppercase;
    text-shadow: 0 0 10px rgba(255, 64, 129, 0.5);
    margin-bottom: 20px;
  }
  
  p {
    font-size: clamp(1em, 2.5vw, 1.2em);
    color: #b0b0c0;
    font-weight: 500;
  }
`;

const CategoriesGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 25px;
  margin-bottom: 40px;
`;

const CategoryCard = styled(motion.div)`
  background: linear-gradient(135deg, rgba(40, 40, 60, 0.9), rgba(50, 50, 70, 0.9));
  border: 2px solid rgba(255, 64, 129, 0.3);
  border-radius: 15px;
  padding: 25px;
  transition: all 0.3s ease;
  cursor: pointer;
  backdrop-filter: blur(10px);
  
  &:hover {
    transform: translateY(-5px);
    border-color: rgba(255, 64, 129, 0.6);
    box-shadow: 0 10px 30px rgba(255, 64, 129, 0.3);
  }
  
  .category-header {
    display: flex;
    align-items: center;
    margin-bottom: 15px;
    
    i {
      font-size: 2em;
      color: #ff4081;
      margin-right: 15px;
      text-shadow: 0 0 10px rgba(255, 64, 129, 0.5);
    }
    
    h3 {
      font-family: 'Roboto', sans-serif;
      font-size: clamp(1.2em, 3vw, 1.4em);
      color: #ff4081;
      margin: 0;
      font-weight: 600;
      text-transform: uppercase;
    }
  }
  
  .category-description {
    font-size: clamp(0.9em, 2vw, 1em);
    color: #b0b0c0;
    margin-bottom: 15px;
    line-height: 1.5;
  }
  
  .category-stats {
    display: flex;
    justify-content: space-between;
    font-size: clamp(0.8em, 1.8vw, 0.9em);
    color: #e0e0e0;
    
    .stat {
      display: flex;
      align-items: center;
      gap: 5px;
      
      i {
        font-size: 1em;
        color: #ff4081;
      }
    }
  }
`;

const RecentActivity = styled(motion.div)`
  background: rgba(40, 40, 60, 0.9);
  border: 1px solid rgba(255, 64, 129, 0.3);
  border-radius: 15px;
  padding: 25px;
  backdrop-filter: blur(10px);
  
  h2 {
    font-family: 'Press Start 2P', cursive;
    font-size: clamp(1.2em, 3vw, 1.6em);
    color: #ff4081;
    margin-bottom: 20px;
    text-transform: uppercase;
  }
`;

const ActivityItem = styled(motion.div)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 0;
  border-bottom: 1px solid rgba(255, 64, 129, 0.2);
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateX(5px);
  }
  
  &:last-child {
    border-bottom: none;
  }
  
  .activity-info {
    flex: 1;
    
    h4 {
      font-family: 'Roboto', sans-serif;
      font-size: clamp(1em, 2.5vw, 1.1em);
      color: #ff4081;
      margin-bottom: 5px;
      font-weight: 600;
    }
    
    p {
      font-size: clamp(0.8em, 2vw, 0.9em);
      color: #b0b0c0;
      margin: 0;
    }
  }
  
  .activity-time {
    font-size: clamp(0.8em, 2vw, 0.9em);
    color: #e0e0e0;
    text-align: right;
  }
`;

const Forums = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const forumCategories = [
    {
      id: 'announcements',
      icon: 'fas fa-bullhorn',
      title: 'Announcements',
      description: 'Official server news, updates, and important information',
      threads: 15,
      posts: 234,
      lastPost: '2 hours ago'
    },
    {
      id: 'general',
      icon: 'fas fa-comments',
      title: 'General Discussion',
      description: 'General chat about Tower of Bedrock and Minecraft',
      threads: 456,
      posts: 2341,
      lastPost: '5 minutes ago'
    },
    {
      id: 'guides',
      icon: 'fas fa-book',
      title: 'Guides & Tutorials',
      description: 'Player-created guides and helpful tutorials',
      threads: 89,
      posts: 567,
      lastPost: '1 hour ago'
    },
    {
      id: 'guild-recruitment',
      icon: 'fas fa-users',
      title: 'Guild Recruitment',
      description: 'Find a guild or recruit new members',
      threads: 123,
      posts: 890,
      lastPost: '30 minutes ago'
    },
    {
      id: 'suggestions',
      icon: 'fas fa-lightbulb',
      title: 'Suggestions',
      description: 'Share your ideas to improve the server',
      threads: 67,
      posts: 345,
      lastPost: '3 hours ago'
    },
    {
      id: 'bug-reports',
      icon: 'fas fa-bug',
      title: 'Bug Reports',
      description: 'Report bugs and technical issues',
      threads: 34,
      posts: 156,
      lastPost: '6 hours ago'
    }
  ];

  const recentActivity = [
    {
      title: 'New Floor 8 Boss Strategy',
      author: 'DragonSlayer99',
      category: 'Guides & Tutorials',
      time: '5 minutes ago'
    },
    {
      title: 'The Ascendants - Recruiting Active Players',
      author: 'GuildMaster',
      category: 'Guild Recruitment',
      time: '15 minutes ago'
    },
    {
      title: 'Suggestion: Add More PvP Areas',
      author: 'PvPKing',
      category: 'Suggestions',
      time: '30 minutes ago'
    },
    {
      title: 'Energy System Bug Fix',
      author: 'Admin',
      category: 'Announcements',
      time: '1 hour ago'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.4,
        staggerChildren: 0.08
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 15 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.4, ease: "easeOut" }
    }
  };

  return (
    <ForumsContainer
      ref={ref}
      variants={containerVariants}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
    >
      <Container>
        <ForumHeader variants={itemVariants}>
          <h1>Community Forums</h1>
          <p>Connect with fellow adventurers and share your Tower of Bedrock experience</p>
        </ForumHeader>
        
        <CategoriesGrid>
          {forumCategories.map((category, index) => (
            <CategoryCard
              key={category.id}
              variants={itemVariants}
              as={Link}
              to={`/forums/${category.id}`}
              whileHover={{ 
                y: -5,
                transition: { duration: 0.3 }
              }}
            >
              <div className="category-header">
                <i className={category.icon}></i>
                <h3>{category.title}</h3>
              </div>
              <p className="category-description">{category.description}</p>
              <div className="category-stats">
                <div className="stat">
                  <i className="fas fa-comments"></i>
                  <span>{category.threads} threads</span>
                </div>
                <div className="stat">
                  <i className="fas fa-reply"></i>
                  <span>{category.posts} posts</span>
                </div>
                <div className="stat">
                  <i className="fas fa-clock"></i>
                  <span>{category.lastPost}</span>
                </div>
              </div>
            </CategoryCard>
          ))}
        </CategoriesGrid>

        <RecentActivity variants={itemVariants}>
          <h2>Recent Activity</h2>
          {recentActivity.map((activity, index) => (
            <ActivityItem
              key={index}
              variants={itemVariants}
              whileHover={{ 
                x: 5,
                transition: { duration: 0.2 }
              }}
            >
              <div className="activity-info">
                <h4>{activity.title}</h4>
                <p>by {activity.author} in {activity.category}</p>
              </div>
              <div className="activity-time">{activity.time}</div>
            </ActivityItem>
          ))}
        </RecentActivity>
      </Container>
    </ForumsContainer>
  );
};

export default Forums;
