import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const ForumsContainer = styled(motion.div)`
  min-height: 100vh;
  padding: 180px 20px 60px;
  background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuMjHxIGmVAAAAMElEQVQYV2NgoBJwcnJyanJycvL///8/AQwMTEwsLCwsLCwsLCwsLCwsLCwsLCwsDI4GAV8HAFrpV3gAAAAASUVORK5CYII=') repeat;
  background-size: 16px 16px;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 clamp(20px, 5vw, 50px);
`;

const ForumHeader = styled(motion.div)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(40, 40, 60, 0.9);
  padding: 15px 20px;
  border-radius: 10px 10px 0 0;
  border: 1px solid rgba(255, 64, 129, 0.3);
  border-bottom: none;
  backdrop-filter: blur(10px);
  
  h2 {
    font-family: 'Press Start 2P', cursive;
    font-size: clamp(1.2em, 3vw, 1.8em);
    color: #ff4081;
    margin: 0;
    text-transform: uppercase;
  }
  
  .stats {
    font-family: 'Montserrat', sans-serif;
    font-size: clamp(0.8em, 2vw, 0.9em);
    color: #b0b0c0;
  }
  
  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
  }
`;

const ForumList = styled(motion.div)`
  background: rgba(40, 40, 60, 0.9);
  border: 1px solid rgba(255, 64, 129, 0.3);
  border-top: none;
  border-radius: 0 0 10px 10px;
  overflow: hidden;
  backdrop-filter: blur(10px);
`;

const ForumThread = styled(motion.div)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 20px;
  border-bottom: 1px solid rgba(255, 64, 129, 0.2);
  transition: all 0.3s ease;
  cursor: pointer;
  
  &:hover {
    background: rgba(50, 50, 70, 0.9);
    transform: translateX(5px);
    box-shadow: inset 5px 0 0 rgba(255, 64, 129, 0.5);
  }
  
  &:last-child {
    border-bottom: none;
  }
`;

const ThreadInfo = styled.div`
  flex: 1;
  
  h3 {
    font-family: 'Roboto', sans-serif;
    font-size: clamp(1em, 2.5vw, 1.2em);
    color: #ff4081;
    margin-bottom: 5px;
    font-weight: 600;
  }
  
  p {
    font-size: clamp(0.8em, 2vw, 0.9em);
    color: #b0b0c0;
    margin: 0;
  }
`;

const ThreadStats = styled.div`
  text-align: right;
  font-family: 'Montserrat', sans-serif;
  font-size: clamp(0.8em, 2vw, 0.9em);
  color: #e0e0e0;
  
  span {
    display: block;
    color: #b0b0c0;
  }
`;

const Forums = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const forumThreads = [
    {
      title: "Welcome to Tower of Bedrock!",
      author: "Admin",
      replies: 45,
      views: 1250,
      lastPost: "2 hours ago"
    },
    {
      title: "Floor 7 Strategy Discussion",
      author: "DragonSlayer99",
      replies: 23,
      views: 890,
      lastPost: "5 hours ago"
    },
    {
      title: "Guild Recruitment - The Ascendants",
      author: "GuildMaster",
      replies: 12,
      views: 456,
      lastPost: "1 day ago"
    },
    {
      title: "Best Enchantments for PvP",
      author: "PvPKing",
      replies: 67,
      views: 2100,
      lastPost: "3 hours ago"
    },
    {
      title: "Bug Report: Energy System Issues",
      author: "BugHunter",
      replies: 8,
      views: 234,
      lastPost: "6 hours ago"
    },
    {
      title: "Upcoming Events & Competitions",
      author: "EventManager",
      replies: 34,
      views: 1567,
      lastPost: "4 hours ago"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  return (
    <ForumsContainer
      ref={ref}
      variants={containerVariants}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
    >
      <Container>
        <ForumHeader variants={itemVariants}>
          <h2>Community Forums</h2>
          <div className="stats">1,234 threads â€¢ 5,678 posts</div>
        </ForumHeader>
        
        <ForumList>
          {forumThreads.map((thread, index) => (
            <ForumThread
              key={index}
              variants={itemVariants}
              whileHover={{ 
                x: 5,
                transition: { duration: 0.2 }
              }}
            >
              <ThreadInfo>
                <h3>{thread.title}</h3>
                <p>by {thread.author}</p>
              </ThreadInfo>
              <ThreadStats>
                <div>{thread.replies} replies</div>
                <span>{thread.views} views</span>
                <span>{thread.lastPost}</span>
              </ThreadStats>
            </ForumThread>
          ))}
        </ForumList>
      </Container>
    </ForumsContainer>
  );
};

export default Forums;
