const ThreadsContainer = styled(motion.div)`
  background: rgba(40, 40, 60, 0.9);
  border: 1px solid rgba(255, 64, 129, 0.3);
  border-radius: 15px;
  overflow: hidden;
  backdrop-filter: blur(10px);
`;

const ThreadsHeader = styled.div`
  background: rgba(50, 50, 70, 0.9);
  padding: 15px 20px;
  border-bottom: 1px solid rgba(255, 64, 129, 0.3);
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  h2 {
    font-family: 'Roboto', sans-serif;
    font-size: clamp(1.1em, 2.5vw, 1.3em);
    color: #ff4081;
    margin: 0;
    font-weight: 600;
    text-transform: uppercase;
  }
  
  .sort-options {
    display: flex;
    gap: 15px;
    font-size: clamp(0.8em, 2vw, 0.9em);
    
    button {
      background: none;
      border: none;
      color: #b0b0c0;
      cursor: pointer;
      transition: color 0.3s ease;
      font-family: 'Roboto', sans-serif;
      
      &:hover, &.active {
        color: #ff4081;
      }
    }
  }
`;

const ThreadItem = styled(motion.div)`
  display: flex;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid rgba(255, 64, 129, 0.2);
  transition: all 0.3s ease;
  cursor: pointer;
  
  &:hover {
    background: rgba(50, 50, 70, 0.9);
    transform: translateX(5px);
    box-shadow: inset 5px 0 0 rgba(255, 64, 129, 0.5);
  }
  
  &:last-child {
    border-bottom: none;
  }
  
  .thread-icon {
    font-size: 1.5em;
    color: #ff4081;
    margin-right: 15px;
    min-width: 30px;
    text-align: center;
  }
  
  .thread-content {
    flex: 1;
    
    .thread-title {
      font-family: 'Roboto', sans-serif;
      font-size: clamp(1em, 2.5vw, 1.2em);
      color: #ff4081;
      margin-bottom: 5px;
      font-weight: 600;
      text-decoration: none;
      
      &:hover {
        text-decoration: underline;
      }
    }
    
    .thread-meta {
      font-size: clamp(0.8em, 2vw, 0.9em);
      color: #b0b0c0;
      
      .author {
        color: #e0e0e0;
        font-weight: 500;
      }
    }
  }
  
  .thread-stats {
    display: flex;
    gap: 20px;
    text-align: center;
    font-family: 'Roboto', sans-serif;
    font-size: clamp(0.8em, 2vw, 0.9em);
    
    .stat {
      display: flex;
      flex-direction: column;
      
      .number {
        color: #ff4081;
        font-weight: 600;
        font-size: 1.1em;
      }
      
      .label {
        color: #b0b0c0;
        font-size: 0.8em;
        text-transform: uppercase;
      }
    }
  }
  
  .last-post {
    text-align: right;
    font-size: clamp(0.8em, 2vw, 0.9em);
    color: #e0e0e0;
    min-width: 120px;
    
    .time {
      color: #b0b0c0;
      display: block;
      margin-top: 2px;
    }
  }
`;

const CreateThreadButton = styled(motion.button)`
  position: fixed;
  bottom: 30px;
  right: 30px;
  background: linear-gradient(135deg, #ff4081, #e03570);
  border: none;
  border-radius: 50px;
  padding: 15px 25px;
  color: white;
  font-family: 'Roboto', sans-serif;
  font-weight: 600;
  font-size: 1em;
  cursor: pointer;
  box-shadow: 0 5px 20px rgba(255, 64, 129, 0.4);
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 10px;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(255, 64, 129, 0.6);
  }
  
  i {
    font-size: 1.2em;
  }
`;

const ForumCategory = () => {
  const { categoryId } = useParams();
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // Mock data - would come from API
  const categoryData = {
    'announcements': {
      icon: 'fas fa-bullhorn',
      title: 'Announcements',
      description: 'Official server news, updates, and important information from the Tower of Bedrock team.',
      threads: 15,
      posts: 234,
      followers: 1250
    },
    'general': {
      icon: 'fas fa-comments',
      title: 'General Discussion',
      description: 'General chat about Tower of Bedrock, Minecraft, and community discussions.',
      threads: 456,
      posts: 2341,
      followers: 890
    },
    'guides': {
      icon: 'fas fa-book',
      title: 'Guides & Tutorials',
      description: 'Player-created guides, tutorials, and helpful tips for Tower of Bedrock.',
      threads: 89,
      posts: 567,
      followers: 445
    }
  };

  const threads = [
    {
      id: 1,
      title: 'Welcome to Tower of Bedrock - Read This First!',
      author: 'Admin',
      replies: 45,
      views: 1250,
      lastPost: 'Admin',
      lastPostTime: '2 hours ago',
      isPinned: true,
      isLocked: false
    },
    {
      id: 2,
      title: 'Server Rules and Guidelines - Updated Feb 2025',
      author: 'Moderator',
      replies: 12,
      views: 890,
      lastPost: 'PlayerName',
      lastPostTime: '5 hours ago',
      isPinned: true,
      isLocked: true
    },
    {
      id: 3,
      title: 'Floor 7 Strategy Discussion - Share Your Tips!',
      author: 'DragonSlayer99',
      replies: 23,
      views: 567,
      lastPost: 'GuildMaster',
      lastPostTime: '1 hour ago',
      isPinned: false,
      isLocked: false
    },
    {
      id: 4,
      title: 'Best Enchantments for PvP Combat',
      author: 'PvPKing',
      replies: 67,
      views: 2100,
      lastPost: 'WarriorX',
      lastPostTime: '3 hours ago',
      isPinned: false,
      isLocked: false
    },
    {
      id: 5,
      title: 'Guild Wars Event - This Weekend!',
      author: 'EventManager',
      replies: 34,
      views: 1567,
      lastPost: 'GuildLeader',
      lastPostTime: '30 minutes ago',
      isPinned: false,
      isLocked: false
    }
  ];

  const category = categoryData[categoryId] || categoryData['general'];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.4,
        staggerChildren: 0.08
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 15 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.4, ease: "easeOut" }
    }
  };

  return (
    <CategoryContainer
      ref={ref}
      variants={containerVariants}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
    >
      <Container>
        <Breadcrumb variants={itemVariants}>
          <Link to="/forums">Forums</Link>
          <span className="separator">â€º</span>
          <span className="current">{category.title}</span>
        </Breadcrumb>

        <CategoryHeader variants={itemVariants}>
          <div className="header-content">
            <i className={category.icon}></i>
            <h1>{category.title}</h1>
          </div>
          <p className="description">{category.description}</p>
          <div className="stats">
            <div className="stat">
              <i className="fas fa-comments"></i>
              <span>{category.threads} threads</span>
            </div>
            <div className="stat">
              <i className="fas fa-reply"></i>
              <span>{category.posts} posts</span>
            </div>
            <div className="stat">
              <i className="fas fa-users"></i>
              <span>{category.followers} followers</span>
            </div>
          </div>
        </CategoryHeader>

        <ThreadsContainer variants={itemVariants}>
          <ThreadsHeader>
            <h2>Threads</h2>
            <div className="sort-options">
              <button className="active">Latest</button>
              <button>Popular</button>
              <button>Most Replies</button>
            </div>
          </ThreadsHeader>

          {threads.map((thread, index) => (
            <ThreadItem
              key={thread.id}
              variants={itemVariants}
              as={Link}
              to={`/forums/${categoryId}/${thread.id}`}
              whileHover={{ 
                x: 5,
                transition: { duration: 0.2 }
              }}
            >
              <div className="thread-icon">
                {thread.isPinned ? (
                  <i className="fas fa-thumbtack"></i>
                ) : thread.isLocked ? (
                  <i className="fas fa-lock"></i>
                ) : (
                  <i className="fas fa-comment"></i>
                )}
              </div>
              
              <div className="thread-content">
                <div className="thread-title">{thread.title}</div>
                <div className="thread-meta">
                  Started by <span className="author">{thread.author}</span>
                </div>
              </div>
              
              <div className="thread-stats">
                <div className="stat">
                  <span className="number">{thread.replies}</span>
                  <span className="label">Replies</span>
                </div>
                <div className="stat">
                  <span className="number">{thread.views}</span>
                  <span className="label">Views</span>
                </div>
              </div>
              
              <div className="last-post">
                <div>{thread.lastPost}</div>
                <span className="time">{thread.lastPostTime}</span>
              </div>
            </ThreadItem>
          ))}
        </ThreadsContainer>
      </Container>

      <CreateThreadButton
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <i className="fas fa-plus"></i>
        New Thread
      </CreateThreadButton>
    </CategoryContainer>
  );
};

export default ForumCategory;
