import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const EventsContainer = styled(motion.div)`
  background: rgba(40, 40, 60, 0.9);
  padding: 30px;
  border-radius: 15px;
  border: 2px solid rgba(255, 64, 129, 0.4);
  text-align: center;
  margin-bottom: 60px;
  box-shadow: inset 0 0 20px rgba(255, 64, 129, 0.1);
  backdrop-filter: blur(10px);
`;

const EventsTitle = styled(motion.h2)`
  font-family: 'Press Start 2P', cursive;
  font-size: clamp(1.4em, 4vw, 1.8em);
  color: #ff4081;
  margin-bottom: 30px;
  text-shadow: 0 0 5px rgba(255, 64, 129, 0.3);
  text-transform: uppercase;
`;

const EventsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 25px;
  
  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const EventItem = styled(motion.div)`
  background: rgba(50, 50, 70, 0.9);
  padding: 20px;
  border-radius: 10px;
  border: 1px solid rgba(255, 64, 129, 0.2);
  position: relative;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  
  &:hover {
    transform: translateY(-5px) rotateX(5deg) rotateY(5deg);
    box-shadow: 0 10px 30px rgba(255, 64, 129, 0.3);
  }
  
  i {
    font-size: clamp(2.5em, 5vw, 3em);
    color: #ff4081;
    margin-bottom: 15px;
    text-shadow: 0 0 10px rgba(255, 64, 129, 0.3);
    transition: transform 0.3s ease;
  }
  
  &:hover i {
    transform: scale(1.1) rotate(10deg);
  }
  
  h3 {
    font-family: 'Roboto', sans-serif;
    font-size: clamp(1.2em, 3vw, 1.4em);
    color: #ff4081;
    margin-bottom: 15px;
    font-weight: 600;
  }
`;

const Countdown = styled.div`
  font-size: clamp(0.9em, 2vw, 1em);
  color: #e0e0e0;
  display: flex;
  justify-content: center;
  gap: 5px;
  align-items: center;
  flex-wrap: wrap;
  
  .label {
    margin-right: 5px;
    color: #b0b0c0;
  }
  
  span:not(.label) {
    background: rgba(255, 64, 129, 0.2);
    padding: 2px 6px;
    border-radius: 5px;
    min-width: 30px;
    text-align: center;
    font-weight: 600;
    font-family: 'Orbitron', sans-serif;
    
    &.update {
      animation: countdownFlip 0.5s ease;
    }
  }
`;

const EventsSection = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.2
  });

  const [countdowns, setCountdowns] = useState({
    doubleExp: { hours: 37, minutes: 0, seconds: 0 },
    outpost: { hours: 6, minutes: 0, seconds: 0 },
    gemSale: { hours: 48, minutes: 0, seconds: 0 }
  });

  useEffect(() => {
    const interval = setInterval(() => {
      setCountdowns(prev => {
        const newCountdowns = { ...prev };
        
        Object.keys(newCountdowns).forEach(key => {
          const countdown = newCountdowns[key];
          if (countdown.seconds > 0) {
            countdown.seconds--;
          } else if (countdown.minutes > 0) {
            countdown.minutes--;
            countdown.seconds = 59;
          } else if (countdown.hours > 0) {
            countdown.hours--;
            countdown.minutes = 59;
            countdown.seconds = 59;
          }
        });
        
        return newCountdowns;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  const events = [
    {
      icon: 'fas fa-star',
      title: 'Double EXP Weekend',
      countdown: countdowns.doubleExp,
      label: 'Ends in:'
    },
    {
      icon: 'fas fa-flag-checkered',
      title: 'Outpost Siege',
      countdown: countdowns.outpost,
      label: 'Starts in:'
    },
    {
      icon: 'fas fa-gem',
      title: 'Gem Sale',
      countdown: countdowns.gemSale,
      label: 'Ends in:'
    }
  ];

  return (
    <EventsContainer
      ref={ref}
      variants={containerVariants}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
    >
      <EventsTitle variants={itemVariants}>
        Featured Events
      </EventsTitle>
      <EventsGrid>
        {events.map((event, index) => (
          <EventItem
            key={index}
            variants={itemVariants}
            whileHover={{ 
              scale: 1.05,
              rotateX: 5,
              rotateY: 5,
              transition: { duration: 0.3 }
            }}
          >
            <i className={event.icon}></i>
            <h3>{event.title}</h3>
            <Countdown>
              <span className="label">{event.label}</span>
              <span>{String(event.countdown.hours).padStart(2, '0')}</span>:
              <span>{String(event.countdown.minutes).padStart(2, '0')}</span>:
              <span>{String(event.countdown.seconds).padStart(2, '0')}</span>
            </Countdown>
          </EventItem>
        ))}
      </EventsGrid>
    </EventsContainer>
  );
};

export default EventsSection;
